    console.log(props.selectedProject)
    if (props.selectedProject != undefined) {
    var url = '/getIssueKeys/' + props.selectedProject
    console.log('url is' + url)
    fetch(url)
    .then(response => response.json())
  .then(data => 
  setIssueKeys(data));

  

}

