import React from 'react';
  const PomodoroBreakView = (props) => {
    const [modalOpen, setModalOpen] = useState(false);
    const closeModal = () => {
      //props.timerdata.changePomodoroStatus();
      setModalOpen(!modalOpen)
    }
    const makeStop = () => {
        setModalOpen(!modalOpen);
      }
      const toggleModal = () => {
        console.log("trying to close nodal")
        setModalOpen(!modalOpen);
      }
   return (<div>
        Time for a break!
        <Modal
      isOpen={modalOpen}
      onRequestClose={toggleModal}
      contentLabel="My dialog3"
      className="mymodal"
      overlayClassName="myoverlay"
      closeTimeoutMS={500}
    >  
    <button onClick={closeModal} >Are you sure? </button> <button onClick={makeStop} >go back to pomodoro</button>

    </Modal>
      </div>)
  }


export default PomodoroBreakView;
