import React from 'react';

const CacheCleaner = (cacheToRemove) => {

    if (cacheToRemove == 'all'){

    localStorage.clear();   
}
else {
    for (const itemToRemove in cacheToRemove) {
    localStorage.removeItem(itemToRemove)
}
}



}

export default CacheCleaner
